INSERT INTO campeonato (nome) VALUES ('Brasileirão Série A');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (1,'Palmeiras', 'Sociedade Esportiva Palmeiras, conhecida popularmente como Palmeiras, é um clube poliesportivo brasileiro da cidade de São Paulo, capital do estado homônimo. Foi fundado em 26 de agosto de 1914 e suas cores, presentes no escudo e bandeira oficial, são o verde e branco. O vermelho, presente desde sua fundação em 1914, foi excluído durante a Segunda Guerra Mundial, por pressão do governo de Getúlio Vargas, na mesma reunião que formalizou a mudança de nome de Palestra Italia para Palmeiras.', 'assets/times/palmeiras.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (2,'Flamengo', 'O Clube de Regatas do Flamengo (mais conhecido simplesmente como Flamengo, popularmente pelos apelidos de Fla, Mengo e Mengão, e cujo acrônimo é CRF) é uma agremiação poliesportiva brasileira com sede na cidade do Rio de Janeiro, capital do estado homônimo. Fundado no bairro do Flamengo para disputas do esporte remo em 17 de novembro de 1895, tornou-se um dos clubes mais bem-sucedidos e populares do esporte brasileiro, especialmente pelo futebol. Tem como suas cores tradicionais o vermelho e o preto e como seus maiores rivais esportivos o Vasco da Gama, o Fluminense e o Botafogo.', 'assets/times/flamengo.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (3,'Internacional', 'O Sport Club Internacional, mais conhecido como Internacional, e popularmente pelos apelidos de Colorado e Inter de Porto Alegre, é um clube multiesportivo brasileiro com sede na cidade de Porto Alegre, capital do Rio Grande do Sul. Foi fundado em 4 de abril de 1909, pelos irmãos Poppe, com o objetivo de ser uma instituição democrática e sem preconceitos. Tem como suas cores o vermelho e o branco e seus torcedores são conhecidos como Colorados.', 'assets/times/internacional.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (4,'Grêmio', 'O Grêmio Foot-Ball Porto Alegrense, mais conhecido como Grêmio, é um clube de futebol profissional brasileiro sediado em Porto Alegre, no Rio Grande do Sul. Fundado em 15 de setembro de 1903, é considerado um dos maiores e mais tradicionais clubes do Brasil e da América do Sul. Suas cores são azul, preto e branco. Manda seus jogos na Arena do Grêmio, com capacidade para 60.540 pessoas.', 'assets/times/gremio.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (5,'São Paulo', 'O São Paulo Futebol Clube, mais conhecido como São Paulo FC ou simplesmente São Paulo, é um clube poliesportivo brasileiro da cidade de São Paulo, capital do estado homônimo. Foi fundado em 25 de janeiro de 1930, tendo interrompido suas atividades em maio de 1935, e as retomado em dezembro do mesmo ano.', 'assets/times/saopaulo.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (6,'Atlético-MG', 'O Clube Atlético Mineiro, mais conhecido como Atlético Mineiro, Atlético ou pelo apelido Galo, e cujo acrônimo é CAM, é um clube de futebol profissional brasileiro sediado na cidade de Belo Horizonte, Minas Gerais. Foi fundado em 25 de março de 1908 por um grupo de estudantes e suas cores tradicionais são o preto e o branco. Foi inicialmente chamado de Athlético Mineiro Football Club, adotando, em 1913, seu nome definitivo. Seu símbolo e alcunha mais popular é o Galo, mascote oficial desde o final da década de 1930.', 'assets/times/atleticomg.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (7,'Athletico-PR', 'O Club Athletico Paranaense mais conhecido como Athletico Paranaense, cujo acrônimo é CAP, é um clube de futebol do Brasil, da cidade de Curitiba, capital do estado do Paraná. Foi fundado em 26 de março de 1924, a partir da fusão do International Foot-Ball Club e do América Futebol Clube.', 'assets/times/atleticopr.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (8,'Cruzeiro', 'O Cruzeiro Esporte Clube, mais conhecido como Cruzeiro, é uma associação polidesportiva brasileira sediada em Belo Horizonte, Minas Gerais. É considerado um dos maiores clubes de futebol do Brasil e da América do Sul. Fundado em 1921 com o nome de Società Sportiva Palestra Italia, foi rebatizado para seu nome atual em 1942 - em referência ao Cruzeiro do Sul - por imposição do governo federal à época proibiu o uso no país de quaisquer símbolos de Alemanha, Itália e Japão, nações inimigas do Brasil no contexto da Segunda Guerra Mundial.', 'assets/times/cruzeiro.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (9,'Botafogo', 'O Botafogo de Futebol e Regatas, ou simplesmente Botafogo, é uma agremiação poliesportiva brasileira, com sede no bairro homônimo ao clube, na cidade do Rio de Janeiro. Nascido da fusão do Club de Regatas Botafogo (fundado para o remo em 1894) com o Botafogo Football Club (formado para o futebol em 1904), é um dos principais clubes do Brasil. Suas maiores glórias esportivas vêm principalmente do futebol, especialmente entre as décadas de 1950 e de 1960, considerada sua era de ouro.', 'assets/times/botafogo.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (10,'Santos', 'O Santos Futebol Clube, mais conhecido apenas como Santos, é um clube brasileiro fundado em 14 de abril de 1912, com sede na cidade homônima. Inicialmente suas cores seriam o branco, azul e dourado, mas um ano após a sua fundação, ficou decidido que as cores do clube passariam a ser branco e preto. O clube manda as suas partidas no Estádio Urbano Caldeira, mais conhecido como Vila Belmiro. Seus maiores rivais no futebol são o Corinthians, com quem disputa o Clássico Alvinegro; o Palmeiras, com quem disputa o Clássico da Saudade; e o São Paulo, com quem disputa o San-São.', 'assets/times/santos.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (11,'Bahia', 'O Esporte Clube Bahia é um clube desportivo brasileiro de futebol da cidade de Salvador, na Bahia. Conhecido simplesmente como Bahia ou pela sigla ECB, foi fundado em 1º de janeiro de 1931 por ex-jogadores do Clube Bahiano de Tênis e a Associação Atlética da Bahia, agremiações que tinham encerrado suas atividades futebolísticas no final da década de 1920.', 'assets/times/bahia.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (12,'Fluminense', 'O Fluminense Football Club, comumente referido como Fluminense, é um clube multidesportivo brasileiro sediado no bairro de Laranjeiras, localizado na Zona Sul da cidade do Rio de Janeiro, capital do estado homônimo. Fundado em 21 de julho de 1902, tem como principal atividade o futebol, mandando suas partidas no Maracanã. Disputa o Campeonato Carioca, a principal liga estadual do Rio de Janeiro, bem como o Campeonato Brasileiro, principal liga do Brasil.', 'assets/times/fluminence.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (13,'Corinthians', 'O Sport Club Corinthians Paulista, comumente referido como Corinthians, ou ainda pelo seu acrônimo SCCP, é um clube poliesportivo brasileiro da cidade de São Paulo, capital do estado de São Paulo. Foi fundado como uma equipe de futebol no dia 1 de setembro de 1910 por um grupo de operários do bairro Bom Retiro. Seu nome foi inspirado no Corinthian Football Club de Londres, que excursionava pelo Brasil.', 'assets/times/corinthians.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (14,'Chapecoense', 'Associação Chapecoense de Futebol, mais conhecida apenas como Chapecoense, é um clube de futebol brasileiro sediado na cidade de Chapecó, Santa Catarina. Foi fundada em 10 de maio de 1973, com o objetivo de restaurar o futebol na cidade. Sua origem está ligada ao fato de que, na década de 1970, a região possuía apenas alguns times amadores, sendo inexpressiva em relação ao futebol profissional. Com o propósito de reverter esta situação, alguns desportistas, jovens apaixonados pelo esporte, decidiram se reunir para criar um time de futebol profissional para a cidade. De maneira geral, pode-se dizer que a Chapecoense, posteriormente um dos mais bem-sucedidos do futebol catarinense, surgiu da união dos clubes Atlético Chapecoense e Independente', 'assets/times/chape.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (15,'Ceará SC', 'Ceará Sporting Club é um clube poliesportivo brasileiro. Sua sede situa-se na capital do estado do Ceará. O clube foi fundado na noite do dia 2 de junho de 1914, pelas ruas do histórico bairro do Centro da Capital.', 'assets/times/ceara.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (16,'Vasco da Gama', 'O Club de Regatas Vasco da Gama, mais conhecido como Vasco da Gama ou simplesmente Vasco, cujo acrônimo é CRVG, é uma entidade sócio-poliesportiva brasileira com sede na cidade do Rio de Janeiro, fundada em 21 de agosto de 1898 por um grupo de remadores. Inspirados nas celebrações do quarto centenário da descoberta do caminho marítimo para as Índias, ocorrida em 1498, batizaram a nova agremiação com o nome do navegador português que alcançou tal feito, Vasco da Gama. Apesar de ter sido fundado como um "clube de regatas", o Vasco da Gama abrange diversas outras modalidades como atletismo, basquete, futebol de areia e futebol americano, tendo como esporte mais tradicional o futebol. É a agremiação poliesportiva brasileira recordista de medalhas em Jogos Olímpicos (40 no total), com 175 atletas convocados, e em Jogos Paralímpicos (28 no total), sendo reconhecido como clube formador pelo Comitê Paralímpico Brasileiro.', 'assets/times/vasco.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (17,'Sport Recife', 'O Sport Club do Recife (conhecido como Sport e Sport Recife, de monograma SCR) é um clube brasileiro de desportos, situado no bairro da Ilha do Retiro, cidade do Recife, estado de Pernambuco. Foi fundado em 13 de maio de 1905, pelo pernambucano Guilherme de Aquino Fonseca, que viveu por muitos anos na Inglaterra, onde estudou na Universidade de Cambridge e regressou a Pernambuco trazendo consigo o apreço pelo novo esporte daquele país: o futebol', 'assets/times/sport.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (18,'América-MG', 'O América Futebol Clube, mais conhecido como América Mineiro, é um clube desportivo brasileiro da cidade de Belo Horizonte, capital do estado brasileiro de Minas Gerais.', 'assets/times/americamg.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (19,'EC Vitória', 'O Esporte Clube Vitória (conhecido como Vitória e cujo acrônimo é ECV) é um clube multiesportivo brasileiro, sediado na cidade de Salvador, no estado da Bahia. Foi fundado em 13 de maio de 1899 com o nome de Club de Cricket Victoria, mudado para Sport Club Victoria, em 1902, e, finalmente, para o atual nome em 1946. Sendo um dos clubes mais antigos do Brasil, suas cores são o vermelho e o preto, seu mascote é o Leão.', 'assets/times/vitoria.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (20,'Paraná', 'O Paraná Clube é um clube de futebol brasileiro da cidade de Curitiba, estado do Paraná, fundado em 19 de dezembro de 1989, após fusão ocorrida entre os clubes Colorado Esporte Clube e Esporte Clube Pinheiros.', 'assets/times/parana.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (21,'Avaí', 'Avaí Futebol Clube é um clube de futebol brasileiro com sede na cidade de Florianópolis, Santa Catarina. Fundado em 1º de setembro de 1923, o Avaí tem como cores o azul e o branco e seu mascote é um leão, sendo conhecido como o "Leão da Ilha" ou "O Time da Raça". Na temporada de 2024, o clube vai disputar o Campeonato Catarinense, Copa do Brasil e a Série B do Campeonato Brasileiro.', 'assets/times/avai.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (22,'CSA', 'Centro Sportivo Alagoano, também conhecido pela sigla CSA, é uma agremiação esportiva brasileira de futebol, da cidade de Maceió, em Alagoas. Fundado em 7 de setembro de 1913 por um grupo de desportistas, o clube nasceu como Centro Sportivo Sete de Setembro, depois foi rebatizado para Centro Sportivo Floriano Peixoto e em 1918, ganhou seu nome atual.', 'assets/times/csa.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (23,'Goiás', 'Goiás Esporte Clube é uma agremiação esportiva brasileira, sediada na cidade de Goiânia, no estado de Goiás. Tem como cores o verde e o branco (alviverde) e atualmente manda seus jogos no Estádio Hailé Pinheiro (Serrinha), e eventualmente jogando nos estádios Serra Dourada e Olímpico.', 'assets/times/goias.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (24,'Fortaleza', 'O Fortaleza Esporte Clube é um clube poliesportivo da cidade brasileira de Fortaleza. Fundado em 18 de outubro de 1918 por Alcides Santos, é um dos maiores clubes do estado do Ceará e um dos principais da região Nordeste. Tem como principal modalidade o futebol masculino, em que seu maior título é da Série B do Campeonato Brasileiro de 2018. É o maior campeão do estado, com 46 títulos do Campeonato Cearense, sendo pentacampeão de 2019 a 2023. É bicampeão da Copa do Nordeste, em 2019 e 2022, e ganhou a Copa Cidade de Natal de 1946 e o Torneio Norte–Nordeste de 1970. O clube fez campanhas relevantes a nível nacional, tendo sido duas vezes vice-campeão brasileiro, em 1960 e 1968, e duas vezes vice-campeão da Série B, em 2002 e 2004, sendo o time cearense com as melhores campanhas no Campeonato Brasileiro. Foi o primeiro clube do estado a participar da Copa Libertadores da América, em 2022, e o único do Nordeste a disputar a final da Copa Sul-Americana, em 2023.', 'assets/times/fortaleza.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (25,'Bragantino', 'O Red Bull Bragantino, também conhecido como RB Bragantino ou simplesmente Bragantino, é um clube esportivo brasileiro da cidade de Bragança Paulista, interior do estado de São Paulo. É controlado pela empresa multinacional de bebidas energéticas Red Bull.', 'assets/times/bragantino.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (26,'Coritiba', 'O Coritiba Foot Ball Club, mais conhecido como Coritiba, é um clube desportivo brasileiro da cidade de Curitiba. Fundado em 12 de outubro de 1909 por descendentes de alemães, é um dos clubes mais populares do Paraná e tradicionais do Sul do Brasil, sendo o mais antigo do estado, e entre os clubes campeões brasileiros, o terceiro mais antigo da região. Além de ser o clube com mais títulos do Paraná.', 'assets/times/coritiba.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (27,'Atlético-GO', 'Atlético Clube Goianiense, notável como Atlético Goianiense e cujo acrônimo é ACG, é uma agremiação esportiva brasileira da cidade de Goiânia, no estado de Goiás, fundado em 2 de abril de 1937', 'assets/times/atlgo.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (28,'Juventude', 'Esporte Clube Juventude é um clube de futebol brasileiro da cidade de Caxias do Sul, localizada no Rio Grande do Sul.', 'assets/times/juventude.png');
INSERT INTO time (id,nome, descricao, url_foto) VALUES (29,'Cuiabá', 'Cuiabá Esporte Clube é uma SAF de futebol brasileiro sediado na cidade de Cuiabá. Foi fundado no dia 12 de dezembro de 2001 pelo ex-jogador Luís Carlos Tóffoli, mais conhecido como Gaúcho', 'assets/times/cuiaba.png');
